        //var phi = (lon+180)*(Math.PI/180);

        const x = -((radius) * Math.sin(phi)*Math.cos(theta));
        const z = ((radius) * Math.sin(phi)*Math.sin(theta));
        const y = ((radius) * Math.cos(phi));

        console.log([x,y,z]);
           return {x,y,z};
        }




  init = () => {
    globeGroup = new THREE.Group();
    camera = new THREE.PerspectiveCamera(
      90,
      window.innerWidth / window.innerHeight,
      0.01,
      100
    );
    camera.position.z = 6;
    scene = new THREE.Scene();

    renderer = new THREE.WebGLRenderer({ antialias: true, alpha: true });
    // renderer.setClearColor( 0x000000, 0 ); // the default
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.setPixelRatio(Math.min(window.devicePixelRatio, 1));
    renderer.setAnimationLoop(this.animation);

    controls = new OrbitControls(camera, appEl);
    controls.enableDamping = true;

    // setup Map

    const axesHelper = new THREE.AxesHelper( 5 );
scene.add( axesHelper );


    const globeGeo = new THREE.SphereBufferGeometry(1);
    const material = new THREE.MeshBasicMaterial({
      map: new THREE.TextureLoader().load(map),
    });

    const globe = new THREE.Mesh(globeGeo, material);

    let mesh = new THREE.Mesh(
      new THREE.SphereBufferGeometry(0.1, 20, 20),
      new THREE.MeshBasicMaterial({ color: 0xad1001 })
    );

    //let lat = 33.749;
    //let lon = -84.388;
    let lat = 0;
    let lon = 0;
    const vector = this.calcPosFromLatLonRad(1, lat, lon);

//     const { x, y, z } = this.calcPosFromLatLonRad(lat, lon);
